mapper.update:
update interface
set id =#{id}

    , path = #{path}


    , type = #{type}


    , request_method = #{requestMethod}


    , entity_id = #{entityId}


    , param_id = #{paramId}


    , result_id = #{resultId}

where id=#{id}

mapper.select:

select * from interface where deleted=0

    and path = #{path}


    and type = #{type}


    and request_method = #{requestMethod}


    and entity_id = #{entityId}


    and param_id = #{paramId}


    and result_id = #{resultId}

order by id desc

select count(*) from interface where deleted=0

    and path = #{path}


    and type = #{type}


    and request_method = #{requestMethod}


    and entity_id = #{entityId}


    and param_id = #{paramId}


    and result_id = #{resultId}

select * from interface where deleted=0

    and path = #{path}


    and type = #{type}


    and request_method = #{requestMethod}


    and entity_id = #{entityId}


    and param_id = #{paramId}


    and result_id = #{resultId}
