mapper.insert:
insert into user(`id`, `mobile`, `user_type`
, `create_time`, `update_time`

, `password`


    , `openid`


    , `weprogram_code`

)
values(#{id}, #{mobile}, #{userType}, now(), now()

, #{password}


    , #{openid}


    , #{weprogramCode}

)

mapper.update:
update user set id=#{id}, verify_code=null

    ,user_type = #{userType}


    ,password = #{password}


    ,verify_code = #{verifyCode}


    ,ip = #{ip}


    ,last_login_time = #{lastLoginTime}


    ,unionid = #{unionid}


    ,openid = #{openid}


    ,password = #{password}


    ,weprogram_code = #{weprogramCode}

where id=#{id}

update user set mobile=#{mobile}, verify_code=null

    ,unionid = #{unionid}


    ,openid = #{openid}


    ,password = #{password}

where mobile=#{mobile}

mapper.select:

select u.id, u.mobile,u.status,u.last_login_time,u.user_type, u.ip,u.openid, u.unionid from user u
where u.delete_flg=0

    and u.verify_code = #{verifyCode}


    and u.password = #{password}


    and u.mobile = #{mobile}


    and u.weprogram_code = #{weprogramCode}

order by u.id desc

select count(1) from `user` u
where delete_flg=0

    and u.verify_code = #{verifyCode}


    and u.password = #{password}


    and u.mobile = #{mobile}


    and u.weprogram_code = #{weprogramCode}
